import React, {useState} from "react";

const InputElement = () => {
    const [textInput, setTextInput] = useState("")
    return (
        <div>
            <input type="text"
             onChange={(e) => {
                 setTextInput(e.target.value)
             }}
             placeholder="Enter some text" 
             />
        <hr/>
        {textInput}
        </div>
    )
}

export default InputElement;
