import React, { useRef } from "react";

const ImageToggleOnMouseOver = ({ primaryImg, secondaryImg}) => {

    const imageRef = useRef(null);
    return (
        <img
        onMouseOver={() => {
            imageRef.current.src = secondaryImg;
        }} 
        onMouseOut={() => {
            imageRef.current.src
        }}
        src={primaryImg} 
        alt=""
        />
    )
}

export default ImageToggleOnMouseOver;
